      </ion-toolbar>,
      <ion-toolbar>
        <ion-title>Default Title</ion-title>
      </ion-toolbar>,

      // Large title
      <ion-toolbar>
        <ion-title size="large">Large Title</ion-title>
      </ion-toolbar>
    ];
  }
